import  Axios  from 'axios'
import React, { useEffect } from 'react'
import { useState } from 'react'
import './search.css'


function Search () {


    const [userdata, setUserData] = useState([])
    const [query,setQuery]  = useState('')


        useEffect(()=>{
            Axios.post('http://localhost:3001/home/search', {


            }).then((response) => {
                console.log(response)
                setUserData([...userdata,...response.data.usersdata])
                console.log(userdata)


            })
        },[])






    return(
        <div>
            <br/>
            <input placeholder='Enter' onChange={(event)=>setQuery(event.target.value)}/>
            <button onClick={useEffect(() => {
                Axios.post('http://localhost:3001/home/search', {


                }).then((response) => {
                    console.log(response)
                    setUserData([...userdata, ...response.data.usersdata])
                    console.log(userdata)


                })
            }, [])}>Search</button>
            <br/>
            {userdata && userdata.filter(user => {
                if(query == ''){
                    return user
                }else if (user.establishment.toLowerCase().includes(query.toLowerCase())){
                    return user
                }


            }).map((user, index) => {
                <div class="card" key={index}>
                    <img src="img_avatar.png" alt="Avatar" style="width:100%" />
                    <div class="container">
                        <h4><b>{user.firstname}</b></h4>
                        <p>{user.lastname}</p>
                    </div>
                </div>


            })


            }


        </div>


    )
}

export default Search
