import React from "react";
import Axios from 'axios';
//import {Link} from 'react-router-dom'
import { useState } from "react";

import "./signup.css";

function SignUp() {







    const [type,setType] = useState('password');
    const [icon,setIcon] = useState('uil-eye-slash')

    const handleToggle = () => {
        if(type === 'password'){
            setIcon('uil-eye');
            setType('text')
        }
        else{
            setIcon('uil-eye-slash')
            setType('password')
        }
    }




    const [registerData, setRegisterData] = useState({
        email: "",
        password: "",
        firstname:'',
        lastname:'',
        establishment:'',
        confirmation:'',
        ischecked: false
    });

    const register = () => {
        Axios.post('http://localhost:3001/register', {
            firstname: registerData.firstname,
            password: registerData.password,
        }).then((response) => {
            console.log(response);
        })
        .catch(error => {
            console.log(error.response)
        });
    };

    const handleChange = (e) => {
        setRegisterData({ ...registerData, [e.target.name]: e.target.value })
    }

    const handleSubmit = (e) => {
        e.preventDefault();
        console.log(registerData);
    }



    return (
        <div className="container">
            <form onSubmit={handleSubmit}>
                <h2 className="formtitle">
                    S'inscrire
                </h2>
                <div className='control'>
                    <input
                    type='text'
                    className='f-lastname'
                    placeholder="Nom"
                    name='lastname'
                    value={registerData.lastname}
                    onChange={handleChange}
                    />
                </div>

                <div className="control">
                    <input
                    type='text'
                    className='f-firstname'
                    placeholder="Prenom"
                    name='firstname'
                    value={registerData.firstname}
                    onChange={handleChange}
                    />
                </div>

                <div id='box' className="control">
                    <input
                        type="text"
                        className="f-email"
                        placeholder="E-mail"
                        name="email"
                        value={registerData.email}
                        onChange={handleChange}
                    />
                    <i  className="uil uil-envelope icon"></i>
                </div>
                <div className='control'>
                    <select  id='f-establishment' placeholder="Etablissement" name='establishment' value={registerData.establishment}  onChange={handleChange}>
                        <option  value='Etablissement'>Etablissement</option>
                        <option className='op1' value='INPT'>INPT</option>
                        <option className='op1' vlaue='ENSIAS'>ENSIAS</option>
                        <option className='op1' value='EMI'>EMI</option>
                    </select>
                </div>


                <div  id='box' className="control">
                    <input
                        id='password'
                        type={type}
                        className="f-password"
                        placeholder="password"
                        minLength='8'
                        name='password'
                        value={registerData.password}
                        onChange={handleChange}
                    />
                    <i onClick={handleToggle} id='togglePassword' className={icon}></i>
                </div>
                <div  id='box' className="control">
                    <input
                        id='password'
                        type={type}
                        className="f-confirm"
                        placeholder="Confirmer mot de passe"
                        minLength='8'
                        name='confirmation'
                        value={registerData.confirmation}
                        onChange={handleChange}
                    />
                    <i  onClick={handleToggle} id='togglePassword' className={icon}></i>
                </div>

                <li>

                    <input type='checkbox' id='terms' name='ischecked' value={registerData.ischecked} onChange={handleChange} />
                    <p>j'accepte les terms et conditions</p>
                    </li>    



                <div className="g-recaptcha" data-sitekey="6LftKMYgAAAAAAkmgbzhGqCccfAa8TnZVNxQ5r-X"></div>
                <button onClick={register} type='submit' className="logbtn">Soumettre</button>




            </form>
        </div>
    )
}
export default SignUp
