import React from "react";
//import {Link} from 'react-router-dom'
import Axios from 'axios';
import { useState } from "react";
import "./login.css";

function LogIn(){

    const [loginStatus, setLoginStatus] = useState("");

    const [loginData, setLoginData] = useState({
        email: '',
        password: '',
    });


    const login = () => {
        Axios.post('http://localhost:3001/login', {
            email: loginData.email,
            password: loginData.password,
        }).then((response) => {
        if (response.data.message) {
            setLoginStatus(response.data.message);         
        } else {
            return(<div class="success">
                <p><strong>Success</strong>Loged in succesfully</p>
            </div>)

        }
    });
};



    const [type, setType] = useState('password');
    const [icon, setIcon] = useState('uil-eye-slash')

    const handleToggle = () => {
        if (type === 'password') {
            setIcon('uil-eye');
            setType('text')
        }
        else {
            setIcon('uil-eye-slash')
            setType('password')
        }
    }



    const handleChange = (e) =>{
        setLoginData({...loginData,[e.target.name]:e.target.value})
    }

    const handleSubmit = (e) =>{
        e.preventDefault();
        console.log(loginData);
    }



    return(
        <div className="container">
            <form onSubmit={handleSubmit}>
                <h2 className="formtitle">
                    Authentification
                </h2>
               <div className="control">
                    <input
                        type="text"
                        className="f-email"
                        placeholder="E-mail"
                        name="email"
                        value={loginData.email}
                        onChange={handleChange}
                    />
                    <i className="uil uil-envelope icon"></i>
               </div>

                <div className="control">
                    <input
                        type={type}
                        className="f-password"
                        placeholder="password"
                        minLength='8'
                        name='password'
                        value={loginData.password}
                        onChange={handleChange}
                    />
                    <i  onClick={handleToggle} className={icon}></i>
                </div>
                <div className="g-recaptcha" data-sitekey="6LftKMYgAAAAAAkmgbzhGqCccfAa8TnZVNxQ5r-X"></div>
                <button onClick={login} className="logbtn">Soumettre</button><br/>

                   <a  className=" linkpass" href="#reset">mot de passe oubli√® </a>
                   <a className="linkregister" href="#register">S'inscrire</a>
                   <h2>{loginStatus}</h2>


            </form>
        </div>
    )
}
export default LogIn
