import React from 'react'
import { useState } from 'react'
import './search.css'


function Search () {


    //const [userdata, setUserData] = useState([])
    const userdata =[]
    const [query,setQuery]  = useState('')

    const getSearch =()=>{
        Axios.post('http://localhost:3001/home/search',{


        }).then((response)=>{
            console.log(response)
            const L = response.data.usersdata
            console.log(L.length)
            for(let i=0;i<L.length;i++){

                userdata.push({

                    lastname: L[i].lastname,
                    firstname: L[i].firstname,
                    establishment: L[i].establishment
                })
            }
            return userdata


        }).then((userdata)=>{
            userdata.filter(user=>{
                if(query == ''){
                    return user
                }else if(user.establishment.toLowerCase().includes(query.toLocaleLowerCase())){
                    return user
                }
                console.log(user)
            }).map((user, index) => {

                <div class="card" key={index}>
                    <p>{user}</p>
                    <img src="img_avatar.png" alt="Avatar" style="width:100%" />
                    <div class="container">
                        <h4><b>{user.firstname}</b></h4>
                        <p>{user.lastname}</p>
                        <p>hello</p>
                    </div>
                </div>


            })
        })
    }





    return(
        <div>
            <br/>
            <input placeholder='Enter' onChange={(event)=>setQuery(event.target.value)}/>
            <button  onClick={getSearch}>Search</button>
            <p>{userdata[0]}</p>

            {userdata && userdata.filter(user => {
                if(query == ''){
                    return user
                }else if (user.establishment.toLowerCase().includes(query.toLowerCase())){
                    return user
                }


            }).map((user, index) => {
                <div class="card" key={index}>
                    <img src="img_avatar.png" alt="Avatar" style="width:100%" />
                    <div class="container">
                        <h4><b>{user.firstname}</b></h4>
                        <p>{user.lastname}</p>
                        <p>hello</p>
                    </div>
                </div>


            })


            }


        </div>


    )
}

export default Search
